package main

import "fmt"

type ValidParenthesis struct {
	IsValid  bool
	EndIndex int
}

func lvpForIndex(s string, validMap map[int]*ValidParenthesis, startIndex int) *ValidParenthesis {

	if startIndex >= len(s)-1 {
		vp := ValidParenthesis{
			IsValid:  false,
			EndIndex: startIndex,
		}

		validMap[startIndex] = &vp
		return &vp
	}

	if k, found := validMap[startIndex]; found {
		return k
	}

	//case when there is a recurrent call
	if s[startIndex+1] == '(' {
		vp := ValidParenthesis{
			IsValid:  false,
			EndIndex: startIndex,
		}
		validMap[startIndex] = &vp
		subIndex := lvpForIndex(s, validMap, startIndex+1)
		if !subIndex.IsValid || subIndex.EndIndex >= len(s)-1 || s[subIndex.EndIndex+1] != ')' {
			return &vp
		}
		vp.EndIndex = subIndex.EndIndex + 1
		vp.IsValid = true

		if vp.EndIndex+1 >= len(s)-1 || s[vp.EndIndex+1] == ')' {
			return &vp
		}
		subIndex = lvpForIndex(s, validMap, vp.EndIndex+1)
		if !subIndex.IsValid {
			return &vp
		}
		vp.EndIndex = subIndex.EndIndex

		return &vp
	}

	vp := ValidParenthesis{
		IsValid:  true,
		EndIndex: startIndex + 1,
	}
	validMap[startIndex] = &vp

	//default case
	if startIndex+1 >= len(s)-1 || s[startIndex+2] == ')' {
		return &vp
	}

	subIndex := lvpForIndex(s, validMap, startIndex+2)
	if !subIndex.IsValid {
		return &vp
	}
	vp.EndIndex = subIndex.EndIndex

	return &vp
}

func longestValidParentheses(s string) int {

	validMap := make(map[int]*ValidParenthesis)
	longestParenthesis := 0
	index := 0
	for index < len(s) {
		if s[index] == ')' {
			index++
			continue
		}

		if k, found := validMap[index]; found {
			if k.IsValid {
				if 1+k.EndIndex-index > longestParenthesis {
					longestParenthesis = 1 + k.EndIndex - index
				}
				index = k.EndIndex + 1
			}
			index = k.EndIndex + 1
			continue
		}

		result := lvpForIndex(s, validMap, index)
		if result.IsValid {
			if 1+result.EndIndex-index > longestParenthesis {
				longestParenthesis = 1 + result.EndIndex - index
			}
			index = result.EndIndex + 1
		} else {
			index++
		}

	}

	return longestParenthesis
}

